import React, { useEffect, Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getMadeJobs } from '../../actions/job';

const RecJobListing = ({ getMadeJobs, job: { jobs, loading } }) => {
    useEffect(() => {
        getMadeJobs();
    }, []);
    return (
        <Fragment>
            {loading ? <Spinner /> : (
                <Fragment>
                    <h1 >Jobs and Applications</h1>
                    {jobs.length > 0 ? (
                        <Fragment>
                            {jobs.map(job => (
                                <div className="container bg-light m-1 border border-success">
                                    <h3 className="job-title">{job.title}</h3>
                                    <p className="m-0">Salary : {job.salary}</p>
                                    <p className="m-0">Date of Posting : {job.date}</p>
                                    <p className="m-0"> Duration : {job.duration === '0' ? (<span>Indefinite</span>) : (<span>{job.duration}</span>)}</p>
                                    <p className="m-0">Deadline : <Moment format="YYYY/MM/DD">{job.deadline}</Moment></p>
                                    <p className="m-0">Number of Applicants : {job.applications.length}</p>
                                    <p className="m-0">Maximum of Applications : {job.maxap.application}</p>
                                </div>
                            ))}
                        </Fragment>
                    ) : (
                            <Fragment>
                                <h2>No Applied Jobs</h2>
                            </Fragment>
                        )}
                </Fragment>
            )}
        </Fragment>
    )
};

RecJobListing.propTypes = {
    getMadeJobs: PropTypes.func.isRequired,
    job: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
    job: state.job
});

export default connect(mapStateToProps, { getMadeJobs })(RecJobListing);
