import React, { Fragment, useState } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { setAlert } from '../../actions/alert';

const Register = ({ setAlert }) => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        typeofuser: '',
        password: '',
        password2: ''
    });

    const { name, email, typeofuser, password, password2 } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();
        if (password !== password2) {
            setAlert('Passwords Do not match or typeofuser is empty');
        }
        else {
            console.log(formData);
        }
    }

    return (
        <Fragment>
            <h1 className="">Sign Up</h1>
            <p className=""><i className="fa fa-user"></i> Create Your Account</p>
            <form className="form" onSubmit={e => onSubmit(e)}>
                <div className="form-group">
                    <input type="text" placeholder="Name" name="name" value={name} onChange={e => onChange(e)} required />
                </div>
                <div className="form-group">
                    <input type="email" placeholder="Email Address" name="email" value={email} onChange={e => onChange(e)} required />
                </div>
                <div className="form-group">
                    <select name="typeofuser" value={typeofuser} onChange={e => onChange(e)} required>
                        <option typeofuser="">None</option>
                        <option typeofuser="Applicant"> Applicant</option>
                        <option typeofuser="Recruiter">Recruiter</option>
                    </select>
                    <small>Select whether Applicant or Recruiter</small>
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={password}
                        onChange={e => onChange(e)}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        name="password2"
                        value={password2}
                        onChange={e => onChange(e)}
                    />
                </div>
                <input type="submit" className="btn btn-primary" value="Register" />
            </form>
            <p className="m-1">
                Already have an account ? <Link to="/login">Sign In</Link>
            </p>
        </Fragment>
    )
};

export default conenct(null, { setAlert })(Register);
