import React, { useState, Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { setAlert } from '../../actions/alert';

const FilterFeature = ({ setAlert }) => {
    const [formData, SetFormData] = useState({
        typeofjob: '',
        usalary: '',
        lsalary: '',
        duration: ''
    });
    const { typeofjob, usalary, lsalary, duration } = formData;

    const onChange = e => SetFormData({
        ...formData,
        [e.target.name]: e.target.value
    });

    const onSubmit = async e => {
        e.preventDefault();
        // console.log(formData);
        if (parseInt(usalary) < 0 || parseInt(lsalary) < 0 || parseInt(duration) <= 0) {
            setAlert('All fields should be positive integers', 'danger');
        }
        else if (usalary !== '' && lsalary !== '') {
            if (parseInt(lsalary) >= parseInt(usalary)) {
                setAlert('Upper Limit must be greater than lower limit of salary', 'danger');
            }
        }
        else {
            console.log(formData);
        }
    };

    return (
        <Fragment>
            <h3>Filter</h3>
            <form className="form" onSubmit={e => onSubmit(e)}>
                <div className="form-group">
                    <select name="typeofjob" value={typeofjob} onChange={e => onChange(e)} >
                        <option typeofjob=""> None </option>
                        <option typeofjob="Full-Time"> Full Time </option>
                        <option typeofjob="Part-Time"> Part Time </option>
                        <option typeofjob="Work-from-home"> Work From Home </option>
                    </select>
                    <small>Select the Type of Job</small>
                </div>
                <div className="form-group">
                    <input type="text" placeholder="Lower Limit of salary" name="lsalary" value={lsalary} onChange={e => onChange(e)} />
                    <small>Lower Limit of Salary</small>
                </div>
                <div className="form-group">
                    <input type="text" placeholder="Upper Limit of salary" name="usalary" value={usalary} onChange={e => onChange(e)} />
                    <small>Upper Limit of Salary</small>
                </div>
                <div className="form-group">
                    <select name="duration" value={duration} onChange={e => onChange(e)} >
                        <option duration="0"> None </option>
                        <option duration="1"> 1 </option>
                        <option duration="2"> 2 </option>
                        <option duration="3"> 3 </option>
                        <option duration="4"> 4 </option>
                        <option duration="5"> 5 </option>
                        <option duration="6"> 6 </option>
                        <option duration="7"> 7 </option>
                    </select>
                    <small>Duration of Job , must be between 1-7 months</small>
                </div>
            </form>
        </Fragment>
    )
};

FilterFeature.propTypes = {
    setAlert: PropTypes.func.isRequired,
};

export default connect(null, { setAlert })(FilterFeature);
