import React, { useState, Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { sortJobs } from '../../actions/job';

const SortFeature = ({ sortJobs }) => {
    const [Sortdata, SetSortData] = useState({
        sort: ""
    });

    const { sort } = Sortdata;

    const onChange = e => SetSortData({
        ...Sortdata,
        [e.target.name]: e.target.value
    });
    const onSubmit = async e => {
        e.preventDefault();
        console.log(Sortdata);
        sortJobs(sort);
    };

    return (
        <Fragment>
            <form className="form" onSubmit={e => onSubmit(e)}>
                <div className="form-group d-flex">
                    <select name="sort" value={sort} onChange={e => onChange(e)} >
                        <option sort=""> None </option>
                        <option sort="salary 1"> Salary Asc</option>
                        <option sort="salary -1"> Salary Desc</option>
                        <option sort="duration 1"> Duration Asc</option>
                        <option sort="duration -1"> Duration Desc</option>
                        <option sort="rating 1"> Rating Asc</option>
                        <option sort="rating -1"> Rating Desc</option>
                    </select>
                    <button type="submit" className="btn btn-warning m-1"><i className="fa fa-sort"></i></button>
                </div>
            </form>
        </Fragment>
    )
};

SortFeature.propTypes = {
    sortJobs: PropTypes.func.isRequired,
};

export default connect(null, { sortJobs })(SortFeature);
