import React, { useEffect, Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Moment from 'react-moment';
import { getJobs } from '../../actions/job';
import Spinner from '../layout/Spinner';

const JobListing = ({ job: { loading, jobs }, getJobs }) => {
    useEffect(() => {
        getJobs();
    }, []);

    return (
        <Fragment>
            {loading && jobs.length <= 0 ? (
                <Fragment>
                    <Spinner />
                </Fragment>
            ) : (
                    <Fragment>
                        <h1>Job Listing</h1>
                        {jobs.length > 0 ? (
                            <Fragment>
                                {jobs.map(job => (
                                    <div className="container bg-default">
                                        <h3 className="job-title">{job.title}</h3>
                                        <p>Recruiter Name : {job.user.name}</p>
                                        <p>Rating : {job.rating}</p>
                                        <p>Salary : {job.salary}</p>
                                        <p> Duration : {job.duration === '0' ? (<span>Indefinite</span>) : (<span>{job.duration}</span>)}</p>
                                        <p>Deadline : <Moment format="YYYY/MM/DD">{job.deadline}</Moment></p>
                                    </div>
                                ))}
                            </Fragment>
                        ) : (
                                <Fragment>
                                    <h2>No Jobs Available</h2>
                                </Fragment>
                            )}
                    </Fragment>
                )
            }
        </Fragment>

    )
};

JobListing.propTypes = {
    getJobs: PropTypes.func.isRequired,
    job: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
    job: state.job
});

export default connect(mapStateToProps, { getJobs })(JobListing);
